INSERT INTO "NaturalObject" VALUES(2669,2,338,'23::13',343,1560,'bat, large-eared horseshoe','di l  ?','92::694','14/04/1996 09:29:29',NULL,'Boonsong Legakul','HB 3.8 cm, T 1.4 cm (shortest tail of the genus in Thailand), E 2 cm. Similar to ''R. pearsoni'', but smaller. Fur short, not wooly; upperparts light brown, underparts buffy. Anterior noseleaf large, covering upper leaf; lancet triangular. Distr: Nepal, S China, N Vietnam, N Thailand: rare, Borneo, Philippines.',NULL);
INSERT INTO "NaturalObject" VALUES(2670,2,338,'23::13',343,882,'bat, North Malayan horsedhoe','di l  ?',103,'14/04/1996 09:29:29',NULL,'Boonsong Legakul','HB 4-5 cm, T 1.8-2.5 cm, 4-6 g. Fur moderately long; hairs uniformly colored to their bases. Light phase: bright cinnamon above, buffy below; dark phase: brown above, pale brown below; marked contrast between lower & upper parts in both phases. Ears sharply pointed. Distr: Thailand, Indochina, Vietnam: N.',NULL);
INSERT INTO "NaturalObject" VALUES(2671,2,338,'23::13',343,1598,'bat, intermediate horseshoe','di l  ?','92::126::157','14/04/1996 09:29:29',NULL,'Boonsong Legakul','H 6 cm, T 2-3 cm. Noseleaf relatively SMALL, with sella having CONCAVE margins. Upperparts greyish to yellowish brown, with hairs darkest at tips. Wings dark brown; ears medium brown, shorter than head. Distr: wide Nepal to SE Asia (except Philippines?); Vietnam: nationwide.',NULL);
INSERT INTO "NaturalObject" VALUES(2672,2,338,'23::13',343,2017,'bat, lesser brown horseshoe','di l  ?','92::694','14/04/1996 09:29:29',NULL,'Boonsong Legakul','HB 5.5 cm, T 1.7 cm. Resembles ''R. malayanus'' but larger and has LOWER, MORE BLUNT lancet. Upperparts reddish brown; underparts much lighter, markedly contrasting with upperparts. Roosts in dark areas of limestone caves and hollow trees. Distr not common: Cambodia, Thailand C & S, Malaysia, Indonesia. Habitat: dry evergreen forests.',NULL);
INSERT INTO "NaturalObject" VALUES(2673,2,338,'23::13',343,2018,'bat, Thomas'' horseshoe','di l  ?','92::694','14/04/1996 09:29:29',NULL,' ','HB 5 cm, T 20 cm. Pale brown, with individual hairs light yellow at base and medium brown at tip. Resembles ''R. acuminatus'', but somewhat smaller, has a narrower hoseshoe; lancet short & broad. Sella parallel-sided, rather wide. Distr: Burma, S China, Thailand not common, Cambodia, Vietnam: N&S. Habitat: in Thailand roosts in same caves as ''R. stheno'' and ''R. malayanus''.',NULL);
INSERT INTO "NaturalObject" VALUES(2674,2,338,'23::13',343,2019,'bat, Dobson''s horseshoe','di l ?','92::694','30/01/1996 09:29:29',NULL,' ','Distr.: at least Cambodia.',NULL);
INSERT INTO "NaturalObject" VALUES(2675,2,338,'23::13',343,1329,'bat, least horseshoe','di l  ?','92::694','14/04/1996 09:29:29',NULL,'Boonsong Legakul','HB 4 cm, T 1.8 cm. Upperparts brown to pale brown (subspecies ''szechwanus'' dark), underparts lighter. Ears wuite large, extending beyond muzzle when laid flat. Anterior noseleaf broad; sella widest at its base; lancet taller than wide. Distr: from E India to S China, Indochina: wide, Thailand, Malaysia.',NULL);
