INSERT INTO "NaturalObject" VALUES(2727,2,516,'23::15',1507,2061,'dolphin, common','c heo thng',103,'31/01/1996 09:29:29',NULL,' ','Up to 2.8 m, 140 kg; females slightly smaller. Divided into sub-species in different seas. Color, size & length of snout vary from place to place and among individuals. Fins markedly pointed, triangular. In pods of 10-100, sometimes more than 1000. Swims up to 25 knots, often seen playing near ships. Feeds on fish. Gestation 11 mo. Single calf 1-1.3 m long at birth. Sexually mature after 3-4 yrs; lifespan 25-30 yrs. Distr: worldwide except polar seas. Habitat: coastal & open waters, rarely near shore or in shallow waters.',NULL);
INSERT INTO "NaturalObject" VALUES(2728,2,516,'23::15',1508,2062,'dolphin, Eastern bottle-nosed','c heo ?',103,'19/07/1997 09:29:29',NULL,'MacDonal Encycl.',' ',NULL);
