INSERT INTO "NaturalObject" VALUES(3069,1,67,'11::18',8,2312,'','sung c vng','92::65::94','23/08/1998 09:29:29',NULL,'Ho (1970) v.1 p.1028','Resembles ''F. benghalensis'', but leaves smaller and rounder, 3-4 by 4-5 cm (observed in young trees), apex also mucronate. Large, epiphytic when young. Leaves not hairy, tough, 14-30 cm (PPH); petiole large, 2.5-3 cm; stipule 2.5-6 cm. Syconia 1-2, ovate or round-oblong, 2.5-3.5 cm; stalk 1.2-1.8 cm; bract 1 cm. Distr. Thailand: planted as ornament; Vietnam: Ba Ria, Thua Thien.',NULL);
INSERT INTO "NaturalObject" VALUES(3070,2,435,'2::15',1604,2313,'barracuda, barred','c nhng nhiu vy','92::1357','23/06/1997 09:29:29',NULL,' ','30-60 cm.Body brownish-whitish. Vertical light brown (not very clear) across lateral line.',NULL);
INSERT INTO "NaturalObject" VALUES(3071,2,633,'2::15',1672,473,'pomfret, black','c chim en','92::1293::10::120','23/06/1997 09:29:29',NULL,' ','Syn.: ''Pampus niger'' of family ''Stromateidae''. 17-40 cm. Body very high, nearly squarely rhombus, dark grey above, light grey below. Pectoral yellow, dorsal & ventral grey with margin dark grey; caudal fin yellowish with posterior edge dark grey. Distr: Thailand.',NULL);
INSERT INTO "NaturalObject" VALUES(3072,2,246,'2::15',1102,2314,'sole, Commerson''s','','92::1358','23/06/1997 09:29:29',NULL,' ','25-30 cm. Grey-blackish, darker above. Fins black, with white edges.',NULL);
INSERT INTO "NaturalObject" VALUES(3073,2,400,'2::15',1099,1370,'sole, tongue','c bn ?','92::1359','23/06/1997 09:29:29',NULL,' ','10-15 cm. Yellowish-brownish. Brown stripes in parallel along rays of fins.',NULL);
INSERT INTO "NaturalObject" VALUES(3074,2,473,'2::15',1673,2315,'sole, broad','','92::1360','23/06/1997 09:29:29',NULL,' ','10-15 cm. Body grey. Distinguished by numerous broad round greyish PATCHES with a dark spot at center. Body higher than other soles.',NULL);
INSERT INTO "NaturalObject" VALUES(3075,2,421,'2::15',1674,2316,'shark, spot-tail::shark, black-tip','c mp Sorrah','92::1361','23/06/1997 09:29:29',NULL,' ','50-100 cm. Tips of 2 dorsal, pectoral fins and lower lobe of caudal fin BLACK. Body silvery white except darker upper part.',NULL);
INSERT INTO "NaturalObject" VALUES(3076,2,421,'2::15',1674,2317,'shark, blackip reef','c mp ?','92::1361','23/06/1997 09:29:29',NULL,' ','50-100 cm. Similar to ''C. sorrah'' except dark tips of fins grey (not black) and not so clearly defined. Also, body seems to be fatter.',NULL);
INSERT INTO "NaturalObject" VALUES(3077,2,417,'2::15',180,2039,'stingray, blue-spot','c ui Kuhlii','92::1362','23/06/1997 09:29:29',NULL,' ','25-70 cm. Brown. Round SPOTS along center line of body.',NULL);
INSERT INTO "NaturalObject" VALUES(3078,2,417,'2::15',1675,2003,'ray, short-tailed butterfly','c ui ?','92::1363','23/06/1997 09:29:29',NULL,' ','80-100 cm. Very broad from side to side (about twice length from head to tail end). Tail AVEY SHORT, about half of length. Body brownish with numerous light, small spots.',NULL);
INSERT INTO "NaturalObject" VALUES(3079,2,634,'2::15',1676,1660,'parrot fish, bridled','','92::1364','23/06/1997 09:29:29',NULL,' ','25-40 cm. Very similar to ''S. ferrugineus'' but caudal margin of caudal fin slightly CONCAVE. Body oval, dark brown. Also: dorsal fin higher. Fat from side to side. Dorsal find starts from 1/3 body length, running up to base of caudal fin.',NULL);
INSERT INTO "NaturalObject" VALUES(3080,2,634,'2::15',1676,2318,'parrot fish, rusty','','92::1364','23/06/1997 09:29:29',NULL,' ','25-35 cm. Very similar to ''S. frenatus'' and ''S. prasiognathos'' but caudal margin of caudal fin STRAIGHT. Body oval, dark brown. Also: dorsal fin lower. Fat from side to side. Dorsal find starts from 1/3 body length, running up to base of caudal fin.',NULL);
INSERT INTO "NaturalObject" VALUES(3081,2,634,'2::15',1676,2319,'parrot fish, Singapore','','92::1365','23/06/1997 09:29:29',NULL,' ','Undistinguishable from ''S. frenatus''. 25-35 cm. Very similar to ''S. ferrugineus'' but caudal margin of caudal fin slightly CONCAVE. Body oval, dark brown. Also: dorsal fin higher. Fat from side to side. Dorsal find starts from 1/3 body length, running up to base of caudal fin.',NULL);
INSERT INTO "NaturalObject" VALUES(3082,2,261,'2::15',1287,287,'sardine, rainbow','c mi ?','92::1366','24/06/1997 09:29:29',NULL,' ','12-17 cm. Body very slender, dorsal and ventral line continue smoothly with head and tail end. Snout pointed. Green-grey above, greyish below. Dorsal grey, small. Caudal fin grey, deeply lobed.',NULL);
INSERT INTO "NaturalObject" VALUES(3083,2,292,'2::15',1532,2320,'anchovy, Tri','c cm sng::c cm sc tiu','92::1367::10::120','26/08/1999 09:29:29',NULL,'Huong (1991)','5-7 cm. D.15, A.20, P.12, V.7. Slightly elongated, a bit flat from side to side. Body white; a broad shiny silvery stripe running mid-heigth from head to tail at each side; caudal fin green; other fins white. Head relatively large; snout short; eyes relatively large. Mouth wide, slanding downwards. Scales round, easily fall off. Distr Vietnam: N, also brackish waters of Tien and Hau Rivers. Habitat: brackish estuaries.',NULL);
INSERT INTO "NaturalObject" VALUES(3084,2,292,'2::15',1532,2321,'anchovy, Hardenberg''s','c cm ?','92::1368::10::120','24/06/1997 09:29:29',NULL,' ','Very similar to ''S. tri''. 10-12 cm. Much of body grey.',NULL);
INSERT INTO "NaturalObject" VALUES(3085,2,303,'2::15',188,2322,'lizardfish, short-fin','c mi ?','92::1369','24/06/1997 09:29:29',NULL,' ','20-30 cm. Body elongate, cylindrical with lizard-like head and adipose fin. 3 or more rows of teeth on anterior part of outer palatine tooth bands. Pectoral fin just reaching to level of pelvic fin base: pelvic fin rays almost equal in length. Pectoral fin with 14-15 finrays; dorsal fin single, with 11 finrays.  Lateral line scales about 54-63. Back and upper sides brown, lower sides and belly white; sometimes traces of faint darker cross-bars on back; inner side of pelvic fins dusky black, except for their margins; stomach white. Distr. Gulf of Thailand: abundant.<br>',NULL);
INSERT INTO "NaturalObject" VALUES(3086,2,215,'2::12',1276,0,'barb, Waander''s bony lipped','','92::1108','29/06/1997 09:29:29',NULL,' ',' ',NULL);
INSERT INTO "NaturalObject" VALUES(3087,1,176,'11::4',1332,2323,'dona Trining','bm hng::bm ','92::1370::1371::9::149::94','15/07/1999 09:29:29',NULL,'Ho (1970) v.2 p.434; Hop (1993)','2-3m, erect or semi-prostrate. Branches few, with fine hairs like wool. Leaves 15-17cm, opposite, oval, with soft hairs. Flowers in cone, terminal, large; corrolla forms a short tube, apex lobed; calyx tube 2.5-3cm, RED, few hairs, yellowish at base, top 5-lobed. Origin: Congo.',NULL);
INSERT INTO "NaturalObject" VALUES(3088,1,176,'11::4',1332,2324,'','bm trng','92::9::112','09/10/1998 09:29:29',NULL,'Ho (1970) v.2 p.436; Hop (1993)','1-7m. In dense shrubs, much branched, branchlets hairy. Leaves broadly ovate, 15-18 by 3-5 cm; 7 pairs of lateral veins; stipules 5-6mm, forked into 2. Flowers Jul-Sep, in small terminal umbels. Calyx small, GREEN, forms small tubes, with 1 LARGE WHITE sepal nearly round, 6-8 cm, with green veins. Corolla DEEP YELLOW, in NARROW & LONG tube 2.5 cm, ears 5-lobed. Berries Aug-Nov, 10mm. Seeds many. Occurs in wild: Da Lat.',NULL);
INSERT INTO "NaturalObject" VALUES(3089,1,25,'28::18',663,204,'lily, golden spider','thot bo lan','92::9::65::94','14/07/1997 09:29:29',NULL,'Hop (1993)','Not in Ho (1970). Perennial due to onion-like bulb. 4-5 leaves, 60 by 2 cm, shiny green. 5-10 flowers on 30-50 cm long, fat, erect & round stem. Flowers bright yellow, erect; petals close together funnel-like at base, opening wide above. Filaments yellowish, anthers reddish. Origin: China.',NULL);
INSERT INTO "NaturalObject" VALUES(3090,1,25,'28::18',473,2325,'crinum, Sumatra::crinum, red','ti li ta::nng ta','92::1372::9::149::1373','15/07/1999 09:29:29',NULL,'Ho (1970) v.2 p.628; Hop (1993)','50 cm. Leaves broad, thick, 1+ m, acuminate. 20-30 flowers, on slightly flat peduncle, year-round, very fragrant, a common sheath at base. Each flower with a long RED-PURPLE peduncle; corolla forms a tube below, 6 lamina above, REDDISH with purple veins. Stamens red-purple, short.',NULL);
INSERT INTO "NaturalObject" VALUES(3091,1,95,33,1677,2326,'cactus, ?','xng rng da','92::9::65::277','14/07/1997 09:29:29',NULL,'Tran Hop','Stem cylindrical, short, with 18-20 SHALLOW VERTICAL RIDGES, bearing small spines. Numerous slender spines on stem forming networks. Terminals may be constricted, forming globose branch heads. Flowers large, near terminals, color varies; petals many, soft, broad, close together, opening wide above. Var. ''albiflora'': white flowers; ''aurantiaca'': yellow-orange.',NULL);
INSERT INTO "NaturalObject" VALUES(3092,1,95,33,1678,2327,'cactus, ?','xng rng su ring','92::9::65::31::453','14/07/1997 09:29:29',NULL,'Tran Hop','Globose, diam. 12-20 cm. Resembles DURIAN FRUIT: numerous quandrangular pyramid-shaped spines, each bearing small spines (usually 4-6 at base & 1 in middle, or 1-2 very short at base & 2 in middle), young small pines yellow. Flowers small, 2.5 cm, reddish, darker at base.',NULL);
INSERT INTO "NaturalObject" VALUES(3093,1,111,'28::18',426,1847,'life plant','trng sinh  dt','1374::9::105','15/09/1998 09:29:29',NULL,'Ho (1970) v.1 p.762; Tran Hop','Erect. Branches flat, cylindrical or nearly square in cross section. Leaves thick, long-ovate, serrate, petiole long; lamina MOLDY WHITISH GREEN. Inflorescence in large cones; 4 calyx green; 4 petals BRIGHT YELLOW; 8 stamens. Follicles many, 10-12mm. Habitat: dry, hot climates.',NULL);
INSERT INTO "NaturalObject" VALUES(3094,2,516,'23::15',1508,1649,'dolphin, bottle-nosed','c heo ?','92::126::157','19/07/1997 09:29:29',NULL,'MacDonal Encycl.','Up 3.9 m & 560 kg, average 227 kg. Silvery grey. "Beak" short, mouth seems to be always smiling. Lower jaw protrudes farther than the upper. Best known to general public. Lives in pods about a dozen, sometimes several hundred. Enjoys approaching ships and following them at same speed for some time. Also likes playing, jumping out of water & splashing. Can swim at 14 knots, dive for more than 6-7 min. Feeds on all types of fish. Gestation 12 mo.; single calf 1+ m long at birth, nurses for 1+ year. Distr: worldwide. Habitat: all temperate seas.',NULL);
INSERT INTO "NaturalObject" VALUES(3095,2,516,'23::15',1679,2328,'dolphin, Pacific white-sided','c heo ?',103,'19/07/1997 09:29:29',NULL,'MacDonal Encycl.','Up to 2.5 n, 140 kg. Back is black, merging into grey on sides. Underside white, separated clearly from grey sides by a black line running from corners of mouth along lateral fins to anus. Fins pointed; sorsal fins tall, hooked, bicolored. Lives in large pods of average 100, sometimes of several thousand. Gestation 1 year. Single young 1.3 m long. Distr: Northern Pacific. Migrates from northern to warmer waters in autumn, may reach lower latitudes along American coasts than Asian coasts. Habitat: coastal waters, up to 200 km from shore.',NULL);
INSERT INTO "NaturalObject" VALUES(3096,2,516,'23::15',1680,2186,'dolphin, Risso''s','c heo ?',103,'19/07/1997 09:29:29',NULL,'MacDonal Encycl.','Up to 4 m, 700 kg. Color quite variable but usually grey, paler forward of dorsal fin; bluish when younger. Belly has two whitish areas. Coloring fade with age. Usually lives in small groups of 2-5, sometimes 40-50. Does not swim very fast but shows considerable endurance. Can remain under water up to 1/2 hour if necessary, normally surfaces in few minutes. Feeds primarily on cuttlefish. Young born about half mother''s length. Distr: all seas, most frequent in more temperate seas in winter. Habitat: deep waters immediately off coasts.',NULL);
INSERT INTO "NaturalObject" VALUES(3097,2,554,23,1681,2329,'deer ?','hu vng','92::126::693','15/02/1998 09:29:29',NULL,' ','Male 60kg, female 40 kg. Vary rare: some dozens found in Malaysia, only 4 found in Vietnam by ''91. Habitat: moist areas, low mountainous areas. Distr. Vietnam: Truong Son range Ha Tinh.',NULL);
INSERT INTO "NaturalObject" VALUES(3098,2,516,'23::13',1682,2330,'orca::whale, killer','c heo ?',103,'19/07/1997 09:29:29',NULL,'MacDonal Encycl.','Up to 9 m, 9 tons; females somewhat smaller. Black & white coloring distinctive. Dorsal fin VERY LONG, up to 1.7 m in adult males. Distr: all seas, most abundant in sub-Artic and sub-Antartic areas. Lives in pods of 2-8, sometimes 30-40; isolated individuals always males. Attacks any prey: seals, fishes, whales, and other dolphins. Never threatened by predators and quite unafraid of approaching ships. Can swim at 55 kph, make huge leap out of water. Gestation 16 mo. Intelligent, easy to train. Habitat: coastal waters over continental shelf, more rarely in open seas.',NULL);
INSERT INTO "NaturalObject" VALUES(3099,2,516,'23::15',1683,2331,'whale, long-finned pilot','c heo ?',103,'19/07/1997 09:29:29',NULL,'MacDonal Encycl.','Up to 6.4 m, 3 tons; females somewhat shorter & only half weight. Coal-grey, underside paler. Dorsal fin not very high, situated on forward half of body. Lateral fins long, pointed. Forehead markedly bulging, increases in size with age. Lives in pods of 5-20, 100+ in northern waters. Swims to great depth of 1000+ m, can stay submerged for 2 hrs. Feeds on fish, esp. cuttlefish. Gestation 15-16 mo., nurses for 2 yrs. Migratory patterns not known; occasionally beaches itself as suicide (as soon as pushed to water head back to shore). Distr: Atlantic Ocean in norther temperate region. Habitat: coastal waters',NULL);
INSERT INTO "NaturalObject" VALUES(3100,2,515,'23::15',1043,567,'whale, blue','c voi xanh','92::131','19/07/1997 09:29:29',NULL,'MacDonal Encycl.','Probably the largest animal ever lived: average 24 m & 130 tons, can be up to 30 m. Greyish blue with some paler spots; tips & undersides of fins often white. Dorsal fin VERY SMALL. Ventral grooves extend to belly. 270-400 baleen plates in each side of mouth. In groups of 30-50 in vague formation spread over several km. Gestation 11 mo. Single scalf 8m long at birth. Distr: worldwide, esp. S Hemisphere. Migrates to polar regions in spring, moves to open sea in autumn to spend the winter. Feeds mainly on krills. Habitat: oceanic, as far as coasts of Antartica. Nearly extinct.',NULL);
INSERT INTO "NaturalObject" VALUES(3101,2,515,'23::15',1684,2332,'whale, humpback','c voi ?','92::131','19/07/1997 09:29:29',NULL,'MacDonal Encycl.','Average 13 m & 27 tons, may reach 15+ m. Grey-black on back, dark on underside; fins partly white. Pectoral fins HUGE, covered with about 28 ventral grooves. 250-350 pairs of baleen plates. Unafraid of ships, easy to approach esp. when it spends long periods of playing with spectacular leaps out of water. Gestation 12 mo. Young 3-4 m at birth, nurses 11 mo. Distr: all oceans. Habitat: deep waters near coasts; travels between cold & warm waters in summer & winter. One of most threatened species, fewer than 10,000 remain. Illegal hunting continues: it is easy to catch.',NULL);
INSERT INTO "NaturalObject" VALUES(3102,2,516,'23::15',1685,2238,'dolphin, spotted','c heo ?',103,'19/07/1997 09:29:29',NULL,'MacDonal Encycl.','Up to 2.4 m, 115 kg. Upperparts backish; sides geyish gradually fading on underside to almost white. Various elongated dark spots on belly. Fins black; a broad dark stripe running from mouth corner to lateral fins. Rostrum, or :beak:, relatively short, very dark; eyes surrounded by a black ring. Lives in pods of 20-50, hunt fish together. Can swim at 32 kph. Gestation 11-12 mo. Single calf 1-1.3 m at birth. Distr: tropical parts of Atlantic, Pacific, Indian Oceans with water 26-30 C degrees. Habitat: deep ocean waters. Thousands trapped and died in tuna nets in Pacific Ocean.',NULL);
INSERT INTO "NaturalObject" VALUES(3103,1,581,1,254,2333,'mushroom, ?','nm ng c','92::54::9::52::94','23/07/1997 09:29:29',NULL,'PHH',' ',NULL);
INSERT INTO "NaturalObject" VALUES(3104,1,86,28,1686,2334,'','cc qu','92::100','23/07/1997 09:29:29',NULL,'PHH','Creeping. Stem smooth. Leaves deeply lobed into 3-5, each lobe shallowly lobed; many rough white hairs. Flowers white; male flowers in cemes, petals 5; females solitary. Fruits oval, red, 3-6 cm, with shallow grooves; meat green; seeds brown. Habitat: paddy fields, scrubs. Used for delivering mothers as shock depressant.',NULL);
INSERT INTO "NaturalObject" VALUES(3105,2,21,'24::20',1687,2335,'kingfisher, rufous-collared','','92::1375','05/09/1999 09:29:29',NULL,'Boonsong & Round (1991); Quy & Cu (1995)','24cm. GREEN crown, distinct facial pattern, RUFOUS COLLAR (extending to back) and underparts. Bill yellowish. In flight: turquoise-blue rump patch. Male: dark blue wings and upper back. Female: wing coverts and upper back dull green. Distr. Thailand: rare resident S, much reduced; Vietnam: not recorded. Habitat: evergreen forest of lowlands and lower hills.',NULL);
INSERT INTO "NaturalObject" VALUES(3106,2,19,'24::20',114,2336,'cuckoos, banded bay','tm vt vn','92::126::157','27/01/1900 09:29:29',NULL,'Boonsong & Round (1991); Quy & Cu (1995)','23 cm. From immature or hepatic morph of ''C. marulinus'' (much more common): longer bill, broad whitish SUPERCILIUM and strinking dark EYE PATCH. Entire underparts whitish with fine dark bars; upperparts rufous brown, finely barred blackish. Often perch erect. Voice (recorded): diagnostic 4 shrill whistled notes of same interval, first 2 same pitch, last 2 descending or last 3 same lower pitch; "pi pi pew pew". Also ascending series of notes somewhat similar to ''C. marulinus'' but introduced by 2-3 note sequence. Distr. Thailand: uncommon resident N to W and S, also E; Vietnam: occasional resident N, central C and S (Dong Nai).',NULL);
